        JavaTimestampFormatter formatter = new JavaTimestampFormatter();
        Timestamp originalTimestamp = Timestamp.valueOf("2022-01-01 12:00:00");
        Timestamp castTimestamp = formatter.cast(originalTimestamp);
        assertEquals(originalTimestamp, castTimestamp);
