    public void testToArray_ReturnsCopyOfElementDataUpToSize() throws Exception {
        // Arrange:
        ObjectArray objArray = new ObjectArray();

        Object[] testData = new Object[] { "one", "two", "three", "four" };
        int testSize = 3;

        Field elementDataField = ObjectArray.class.getDeclaredField("elementData");
        elementDataField.setAccessible(true);
        elementDataField.set(objArray, testData);

        Field sizeField = ObjectArray.class.getDeclaredField("size");
        sizeField.setAccessible(true);
        sizeField.setInt(objArray, testSize);

        // Act:
        Object[] result = objArray.toArray();

        // Assert:
        assertEquals(testSize, result.length);
        assertArrayEquals(Arrays.copyOf(testData, testSize), result);
        assertNotSame(testData, result);
    }

