@Test
public void testSetConnectTimeout() {
    Request request = new Request(Verbs.GET, "http://example.com");
    request.setConnectTimeout(5000, TimeUnit.MILLISECONDS);

    int expectedTimeout = request.getConnectTimeoutInMillis(); // Fix: Access the connectTimeout value using the getter method
    assertEquals(5000, expectedTimeout);

    request.setConnectTimeout(10, TimeUnit.SECONDS);
    assertEquals(10000, request.getConnectTimeoutInMillis());

    request.setConnectTimeout(1, TimeUnit.MINUTES);
    assertEquals(60000, request.getConnectTimeoutInMillis());
}


    
    
@Test
public void testSetReadTimeout() {
    Request request = new Request(Verbs.GET, "https://example.com");
    request.setReadTimeout(5, TimeUnit.SECONDS);

    int expectedTimeoutInMillis = 5000;
    assertEquals(expectedTimeoutInMillis, request.getReadTimeoutInMillis());

    request.setReadTimeout(1, TimeUnit.MINUTES);
    expectedTimeoutInMillis = 60000;
    assertEquals(expectedTimeoutInMillis, request.getReadTimeoutInMillis());

    request.setReadTimeout(2, TimeUnit.HOURS);
    expectedTimeoutInMillis = 7200000;
    assertEquals(expectedTimeoutInMillis, request.getReadTimeoutInMillis());
}


    
    
