    assertNotNull(result, "Result should not be null");

    assertEquals(result.length(), length, "Result length should match input length");

    for (char ch : result.toCharArray()) {
        assertTrue(ch >= '0' && ch <= '9', "Character should be a digit between 0 and 9");
    }
    String emptyResult = Strings.randomNumber(0);
    assertNotNull(emptyResult, "Result should not be null for zero length");
    assertEquals(emptyResult.length(), 0, "Result length should be zero for zero input");
