public void testFindIntersections() {
    List<Ruling> horizontals = new ArrayList<>();
    List<Ruling> verticals = new ArrayList<>();
    Ruling horizontal1 = new Ruling(new Point2D.Float(0, 0), new Point2D.Float(10, 0));
    Ruling horizontal2 = new Ruling(new Point2D.Float(0, 5), new Point2D.Float(10, 5));
    Ruling vertical1 = new Ruling(new Point2D.Float(2, 0), new Point2D.Float(2, 10));
    Ruling vertical2 = new Ruling(new Point2D.Float(7, 0), new Point2D.Float(7, 10));
    horizontals.add(horizontal1);
    horizontals.add(horizontal2);
    verticals.add(vertical1);
    verticals.add(vertical2);
    Map<Point2D, Ruling[]> intersections = Ruling.findIntersections(horizontals, verticals);

    // We expect 4 intersection points: (2,0), (2,5), (7,0), (7,5)
    // Check the size
    assertEquals(4, intersections.size());

    // Check that each expected point is present
    Point2D.Float p1 = new Point2D.Float(2, 0);
    Point2D.Float p2 = new Point2D.Float(2, 5);
    Point2D.Float p3 = new Point2D.Float(7, 0);
    Point2D.Float p4 = new Point2D.Float(7, 5);

    boolean containsP1 = intersections.keySet().stream().anyMatch(p -> Utils.feq(p.getX(), p1.getX()) && Utils.feq(p.getY(), p1.getY()));
    boolean containsP2 = intersections.keySet().stream().anyMatch(p -> Utils.feq(p.getX(), p2.getX()) && Utils.feq(p.getY(), p2.getY()));
    boolean containsP3 = intersections.keySet().stream().anyMatch(p -> Utils.feq(p.getX(), p3.getX()) && Utils.feq(p.getY(), p3.getY()));
    boolean containsP4 = intersections.keySet().stream().anyMatch(p -> Utils.feq(p.getX(), p4.getX()) && Utils.feq(p.getY(), p4.getY()));

    assertTrue(containsP1);
    assertTrue(containsP2);
    assertTrue(containsP3);
    assertTrue(containsP4);
}
