@Test
public void testSetLeft_AdjustsLeftAndWidthCorrectly() {
    // Create a rectangle with top=10, left=20, width=30, height=40
    Rectangle rect = new Rectangle(10, 20, 30, 40);

    // Store original right edge coordinate
    float originalRight = rect.getRight();

    // Set new left edge to 25 (shift right by 5)
    rect.setLeft(25);

    // The left edge should now be 25
    assertEquals(25, rect.getLeft(), 0.0001);

    // The right edge should remain unchanged
    assertEquals(originalRight, rect.getRight(), 0.0001);

    // The width should be adjusted accordingly (originalRight - newLeft)
    assertEquals(originalRight - 25, rect.getRight() - rect.getLeft(), 0.0001);

    // The top and height should remain unchanged
    assertEquals(10, rect.getTop(), 0.0001);
    assertEquals(40, rect.getBottom() - rect.getTop(), 0.0001);
}
