@Test
public void testSetTop_AdjustsTopAndHeightKeepsBottomFixed() {
    // Initial rectangle: top=10, left=5, width=20, height=15
    Rectangle rect = new Rectangle(10f, 5f, 20f, 15f);
    float originalBottom = rect.getBottom();

    // New top coordinate to set
    float newTop = 5f;
    rect.setTop(newTop);

    // After setTop, top should be updated to newTop
    assertEquals(newTop, rect.getTop(), 0.0001f);

    // Bottom should remain unchanged
    assertEquals(originalBottom, rect.getBottom(), 0.0001f);

    // Height should be adjusted accordingly: newHeight = originalBottom - newTop
    float expectedHeight = originalBottom - newTop;
    assertEquals(expectedHeight, rect.getBottom() - rect.getTop(), 0.0001f);
    assertEquals(expectedHeight, rect.getArea() / rect.getWidth(), 0.0001f);
}
