    @Test
    public void testGetTextWithAndWithoutLineReturns() {
        TestCell cell = new TestCell(0, 0, 100, 50);

        // Add text chunks with different vertical positions
        cell.textElements.add(new TestTextChunk("Hello", 10));
        cell.textElements.add(new TestTextChunk("World", 10));
        cell.textElements.add(new TestTextChunk("Line2", 20));
        cell.textElements.add(new TestTextChunk("Line3", 30));

        // Expected concatenation without line returns: all text concatenated in vertical order without breaks
        String expectedNoLineReturns = "HelloWorldLine2Line3";
        assertEquals(expectedNoLineReturns, cell.getText(false));

        // Expected concatenation with line returns: line breaks inserted between different top positions
        // Since "Hello" and "World" share top=10, no break between them
        // Then line break before "Line2" (top=20), and before "Line3" (top=30)
        String expectedWithLineReturns = "HelloWorld\rLine2\rLine3";
        assertEquals(expectedWithLineReturns, cell.getText(true));
    }
