@Test
public void testBoundingBoxOf() {
    List<Rectangle> rects = new LinkedList<>();
    rects.add(new Rectangle(1f, 1f, 2f, 2f));  // covers (left=1, top=1, right=3, bottom=3)
    rects.add(new Rectangle(0f, 0f, 1f, 1f));  // covers (left=0, top=0, right=1, bottom=1)
    rects.add(new Rectangle(2f, 2f, 3f, 3f));  // covers (left=2, top=2, right=5, bottom=5)

    Rectangle boundingBox = Rectangle.boundingBoxOf(rects);

    // The bounding box should cover from min left=0, min top=0 to max right=5, max bottom=5
    // So top=0, left=0, width=5, height=5
    float expectedTop = 0f;
    float expectedLeft = 0f;
    float expectedWidth = 5f;
    float expectedHeight = 5f;

    // Check top and left
    org.junit.Assert.assertEquals(expectedTop, boundingBox.getTop(), 0.0001);
    org.junit.Assert.assertEquals(expectedLeft, boundingBox.getLeft(), 0.0001);

    // Check width and height by calculating from right and bottom
    float actualWidth = boundingBox.getRight() - boundingBox.getLeft();
    float actualHeight = boundingBox.getBottom() - boundingBox.getTop();

    org.junit.Assert.assertEquals(expectedWidth, actualWidth, 0.0001);
    org.junit.Assert.assertEquals(expectedHeight, actualHeight, 0.0001);
}
