@Test
public void testClip() {
    // Define a clipping window from (1,1) to (4,4)
    Rectangle2D clipWindow = new Rectangle2D.Float(1f, 1f, 3f, 3f);
    CohenSutherlandClipping clipper = new CohenSutherlandClipping(clipWindow);

    // Test 1: Line completely inside the clipping window (should return true, line unchanged)
    Line2D.Float lineInside = new Line2D.Float(2f, 2f, 3f, 3f);
    boolean resultInside = clipper.clip(lineInside);
    assert resultInside;
    // The line should remain the same
    assert lineInside.getX1() == 2f;
    assert lineInside.getY1() == 2f;
    assert lineInside.getX2() == 3f;
    assert lineInside.getY2() == 3f;

    // Test 2: Line partially outside, crossing the clipping window (should return true, line clipped)
    Line2D.Float linePartial = new Line2D.Float(0f, 0f, 5f, 5f);
    boolean resultPartial = clipper.clip(linePartial);
    assert resultPartial;
    // The clipped line should be within the clip window bounds
    assert linePartial.getX1() >= 1f && linePartial.getX1() <= 4f;
    assert linePartial.getY1() >= 1f && linePartial.getY1() <= 4f;
    assert linePartial.getX2() >= 1f && linePartial.getX2() <= 4f;
    assert linePartial.getY2() >= 1f && linePartial.getY2() <= 4f;

    // Test 3: Line completely outside and not intersecting (should return false)
    Line2D.Float lineOutside = new Line2D.Float(5f, 5f, 6f, 6f);
    boolean resultOutside = clipper.clip(lineOutside);
    assert !resultOutside;

    // Test 4: Vertical line crossing the clipping window
    Line2D.Float verticalLine = new Line2D.Float(2f, 0f, 2f, 5f);
    boolean resultVertical = clipper.clip(verticalLine);
    assert resultVertical;
    assert verticalLine.getX1() == 2f && verticalLine.getX2() == 2f;
    assert verticalLine.getY1() >= 1f && verticalLine.getY1() <= 4f;
    assert verticalLine.getY2() >= 1f && verticalLine.getY2() <= 4f;

    // Test 5: Horizontal line crossing the clipping window
    Line2D.Float horizontalLine = new Line2D.Float(0f, 3f, 5f, 3f);
    boolean resultHorizontal = clipper.clip(horizontalLine);
    assert resultHorizontal;
    assert horizontalLine.getY1() == 3f && horizontalLine.getY2() == 3f;
    assert horizontalLine.getX1() >= 1f && horizontalLine.getX1() <= 4f;
    assert horizontalLine.getX2() >= 1f && horizontalLine.getX2() <= 4f;
}
