    @Test
    public void testSmooth() {
        float[] data1 = {1, 2, 3, 4, 5};
        int kernelSize1 = 3;
        float[] expected1 = new float[data1.length];
        int halfKernel = kernelSize1 / 2;

        for (int i = 0; i < data1.length; i++) {
            float sum = 0;
            int start = Math.max(0, i - halfKernel);
            int end = Math.min(i + halfKernel, data1.length);
            for (int j = start; j < end; j++) {
                sum += data1[j];
            }
            expected1[i] = (float) Math.floor(sum / kernelSize1);
        }

        float[] result1 = ProjectionProfile.smooth(data1, kernelSize1);
        assertArrayEquals(expected1, result1, 0.0f);
    }
