@Test
public void testSetBottom_AdjustsHeightCorrectly() {
    // Arrange: create a rectangle with known top, left, width, and height
    float top = 10f;
    float left = 5f;
    float width = 20f;
    float height = 15f;
    Rectangle rect = new Rectangle(top, left, width, height);

    // Act: set a new bottom coordinate
    float newBottom = 40f;
    rect.setBottom(newBottom);

    // Assert:
    // The top should remain unchanged
    org.junit.Assert.assertEquals(top, rect.getTop(), 0.0001f);
    // The left should remain unchanged
    org.junit.Assert.assertEquals(left, rect.getLeft(), 0.0001f);
    // The width should remain unchanged
    org.junit.Assert.assertEquals(width, rect.getRight() - rect.getLeft(), 0.0001f);
    // The height should be adjusted to newBottom - top
    float expectedHeight = newBottom - top;
    org.junit.Assert.assertEquals(expectedHeight, rect.getBottom() - rect.getTop(), 0.0001f);
    // The bottom should be equal to newBottom
    org.junit.Assert.assertEquals(newBottom, rect.getBottom(), 0.0001f);
}
