@Test
public void testIsLtrDominant() {
    // Helper to create TextElement with given text and dummy position/size
    class TE extends TextElement {
        private final String text;
        public TE(String text) {
            super(0, 0, 0, 0);
            this.text = text;
        }
        @Override
        public String getText() {
            return text;
        }
    }

    // Case 1: LTR dominant text
    TextElement ltr1 = new TE("Hello");
    TextElement ltr2 = new TE("World");
    List<TextElement> ltrElements = new ArrayList<>();
    ltrElements.add(ltr1);
    ltrElements.add(ltr2);
    TextChunk ltrChunk = new TextChunk(ltrElements);
    int ltrResult = ltrChunk.isLtrDominant();
    // Expect positive because "HelloWorld" is LTR
    assertTrue("Expected LTR dominant > 0 but got " + ltrResult, ltrResult > 0);

    // Case 2: RTL dominant text (using Hebrew letters, which are RTL)
    TextElement rtl1 = new TE("\u05D0\u05D1\u05D2"); // Hebrew letters Alef, Bet, Gimel
    TextElement rtl2 = new TE("\u05D3\u05D4");       // Hebrew letters Dalet, He
    List<TextElement> rtlElements = new ArrayList<>();
    rtlElements.add(rtl1);
    rtlElements.add(rtl2);
    TextChunk rtlChunk = new TextChunk(rtlElements);
    int rtlResult = rtlChunk.isLtrDominant();
    // Expect negative because Hebrew is RTL
    assertTrue("Expected RTL dominant < 0 but got " + rtlResult, rtlResult < 0);

    // Case 3: Mixed text with equal LTR and RTL characters
    TextElement mixed = new TE("A\u05D0"); // 'A' is LTR, Hebrew Alef is RTL
    TextChunk mixedChunk = new TextChunk(mixed);
    int mixedResult = mixedChunk.isLtrDominant();
    // Expect 0 because equal count of LTR and RTL
    assertEquals("Expected neutral directionality (0) but got " + mixedResult, 0, mixedResult);

    // Case 4: No directional characters (e.g. digits or punctuation)
    TextElement neutral = new TE("12345!@#");
    TextChunk neutralChunk = new TextChunk(neutral);
    int neutralResult = neutralChunk.isLtrDominant();
    // Expect 0 because no LTR or RTL directional chars
    assertEquals("Expected neutral directionality (0) but got " + neutralResult, 0, neutralResult);
}
