@Test
public void testSqueezeSplitsCorrectlyOnConsecutiveChars() {
    // Arrange:
    // Prepare TextElements: sequence with runs of 'a' and other chars
    List<TextElement> elements = new ArrayList<>();
    elements.add(new TextElement("a", 0, 0, 1, 1)); // 0
    elements.add(new TextElement("a", 0, 0, 1, 1)); // 1
    elements.add(new TextElement("b", 0, 0, 1, 1)); // 2
    elements.add(new TextElement("a", 0, 0, 1, 1)); // 3
    elements.add(new TextElement("a", 0, 0, 1, 1)); // 4
    elements.add(new TextElement("a", 0, 0, 1, 1)); // 5
    elements.add(new TextElement("c", 0, 0, 1, 1)); // 6

    TextChunk chunk = new TextChunk(elements);

    // Act:
    List<TextChunk> chunks = chunk.squeeze('a', 2);

    // Assert:
    // We expect the chunk to be split isolating runs of 'a' of length >= 2
    // Runs are at indices 0-1 and 3-5
    // So expected chunks:
    // 1) chunk of first two 'a's (indices 0-1)
    // 2) chunk of 'b' (index 2)
    // 3) chunk of three 'a's (indices 3-5)
    // 4) chunk of 'c' (index 6)

    // Since squeeze is recursive and returns List<TextChunk>, flatten all elements from chunks
    List<TextElement> allElements = new ArrayList<>();
    for (TextChunk tc : chunks) {
        allElements.addAll(tc.getTextElements());
    }

    // The total elements count must be same as original
    assertEquals(elements.size(), allElements.size());

    // Validate each chunk isolates sequences of 'a's of length >=2 or other chars
    for (TextChunk tc : chunks) {
        List<TextElement> teList = tc.getTextElements();
        if (teList.isEmpty()) continue;
        char firstChar = teList.get(0).getText().trim().charAt(0);
        if (firstChar == 'a') {
            // All chars in this chunk should be 'a'
            for (TextElement te : teList) {
                assertEquals('a', te.getText().trim().charAt(0));
            }
            // Length must be at least 2 (minRunLength)
            assertTrue(teList.size() >= 2);
        } else {
            // no run of 'a' here, so no consecutive 'a's of length >= 2
            for (int i = 0; i < teList.size(); i++) {
                char c = teList.get(i).getText().trim().charAt(0);
                assertNotEquals('a', c);
            }
        }
    }
}
