@Test
public void testSort_shouldReturnMapWithEntriesSortedByKey() {
    Map<String, String> input = new HashMap<>();
    input.put("banana", "yellow");
    input.put("apple", "red");
    input.put("cherry", "dark red");

    Map<String, String> sorted = MapUtils.sort(input);

    // The original map should remain unmodified
    assertEquals(3, input.size());
    assertTrue(input.containsKey("banana"));
    assertTrue(input.containsKey("apple"));
    assertTrue(input.containsKey("cherry"));

    // The returned map should be a LinkedHashMap preserving insertion order
    assertTrue(sorted instanceof LinkedHashMap);

    // The keys should be sorted alphabetically
    String[] expectedKeys = {"apple", "banana", "cherry"};
    int i = 0;
    for (String key : sorted.keySet()) {
        assertEquals(expectedKeys[i++], key);
    }

    // The values should correspond to the sorted keys
    assertEquals("red", sorted.get("apple"));
    assertEquals("yellow", sorted.get("banana"));
    assertEquals("dark red", sorted.get("cherry"));
}
