@Test
public void testVerticalOverlap() {
    // Rectangle 1: top=1, left=0, width=2, height=4 (bottom = top + height = 5)
    Rectangle r1 = new Rectangle(1f, 0f, 2f, 4f);
    // Rectangle 2: top=3, left=1, width=2, height=3 (bottom = 6)
    Rectangle r2 = new Rectangle(3f, 1f, 2f, 3f);
    // Overlap vertical segment: from 3 to 5 => length = 2
    float overlap = r1.verticalOverlap(r2);
    org.junit.Assert.assertEquals(2f, overlap, 0.0001f);

    // Rectangle 3: top=6, left=0, width=2, height=2 (bottom = 8)
    Rectangle r3 = new Rectangle(6f, 0f, 2f, 2f);
    // No vertical overlap with r1 (r1 bottom=5, r3 top=6)
    overlap = r1.verticalOverlap(r3);
    org.junit.Assert.assertEquals(0f, overlap, 0.0001f);

    // Rectangle 4: top=0, left=0, width=2, height=10 (bottom=10)
    Rectangle r4 = new Rectangle(0f, 0f, 2f, 10f);
    // r1 is fully inside r4 vertically, overlap = r1 height = 4
    overlap = r1.verticalOverlap(r4);
    org.junit.Assert.assertEquals(4f, overlap, 0.0001f);

    // Rectangle 5: top=4, left=0, width=2, height=1 (bottom=5)
    Rectangle r5 = new Rectangle(4f, 0f, 2f, 1f);
    // Overlap vertical segment: from 4 to 5 => length = 1
    overlap = r1.verticalOverlap(r5);
    org.junit.Assert.assertEquals(1f, overlap, 0.0001f);
}
