@Test
public void testVerticalOverlapRatio() {
    // Case 1: Partial overlap where other starts above this and ends inside this
    Rectangle r1 = new Rectangle(10, 0, 5, 10); // top=10, bottom=20
    Rectangle r2 = new Rectangle(5, 0, 5, 10);  // top=5, bottom=15
    float expected1 = (15 - 10) / 10f; // overlap 5, smaller height 10
    assertEquals(expected1, r1.verticalOverlapRatio(r2), 0.0001f);

    // Case 2: Partial overlap where this starts above other and ends inside other
    Rectangle r3 = new Rectangle(5, 0, 5, 10);  // top=5, bottom=15
    Rectangle r4 = new Rectangle(10, 0, 5, 10); // top=10, bottom=20
    float expected2 = (15 - 10) / 10f; // overlap 5, smaller height 10
    assertEquals(expected2, r3.verticalOverlapRatio(r4), 0.0001f);

    // Case 3: Other fully inside this
    Rectangle r5 = new Rectangle(10, 0, 5, 15); // top=10, bottom=25
    Rectangle r6 = new Rectangle(12, 0, 5, 5);  // top=12, bottom=17
    float expected3 = 5 / 5f; // overlap 5, smaller height 5
    assertEquals(expected3, r5.verticalOverlapRatio(r6), 0.0001f);

    // Case 4: This fully inside other
    Rectangle r7 = new Rectangle(12, 0, 5, 5);  // top=12, bottom=17
    Rectangle r8 = new Rectangle(10, 0, 5, 15); // top=10, bottom=25
    float expected4 = 5 / 5f; // overlap 5, smaller height 5
    assertEquals(expected4, r7.verticalOverlapRatio(r8), 0.0001f);

    // Case 5: No vertical overlap
    Rectangle r9 = new Rectangle(0, 0, 5, 5);   // top=0, bottom=5
    Rectangle r10 = new Rectangle(10, 0, 5, 5); // top=10, bottom=15
    assertEquals(0f, r9.verticalOverlapRatio(r10), 0.0001f);
    assertEquals(0f, r10.verticalOverlapRatio(r9), 0.0001f);

    // Case 6: Identical rectangles
    Rectangle r11 = new Rectangle(10, 0, 5, 10); // top=10, bottom=20
    Rectangle r12 = new Rectangle(10, 0, 5, 10); // top=10, bottom=20
    assertEquals(1f, r11.verticalOverlapRatio(r12), 0.0001f);
}
