@Test
public void testMerge_combinesTextElementsInOrderAndReturnsThis() {
    // Create two RectangularTextContainer instances with dummy coordinates
    RectangularTextContainer<TextElement> container1 = new RectangularTextContainer<TextElement>(0, 0, 10, 10) {
        @Override
        public int compareTo(RectangularTextContainer<TextElement> other) {
            // Define ordering: container1 < container2
            return -1;
        }
    };
    RectangularTextContainer<TextElement> container2 = new RectangularTextContainer<TextElement>(0, 0, 10, 10) {
        @Override
        public int compareTo(RectangularTextContainer<TextElement> other) {
            // Define ordering: container2 > container1
            return 1;
        }
    };

    // Prepare text elements for container1
    TextElement te1 = new TextElement(0, 0, 1, 1, "A");
    List<TextElement> list1 = new java.util.ArrayList<>();
    list1.add(te1);
    container1.setTextElements(list1);

    // Prepare text elements for container2
    TextElement te2 = new TextElement(0, 0, 1, 1, "B");
    List<TextElement> list2 = new java.util.ArrayList<>();
    list2.add(te2);
    container2.setTextElements(list2);

    // Call merge: container1 < container2, so container1's textElements should be first, then container2's appended
    RectangularTextContainer<TextElement> result = container1.merge(container2);

    // Verify that the returned object is the same as container1
    org.junit.Assert.assertSame(container1, result);

    // Verify that container1's text elements now contain both te1 and te2 in order
    List<TextElement> mergedList = container1.getTextElements();
    org.junit.Assert.assertEquals(2, mergedList.size());
    org.junit.Assert.assertEquals(te1, mergedList.get(0));
    org.junit.Assert.assertEquals(te2, mergedList.get(1));

    // Now test the opposite order: container2.merge(container1)
    // Reset container2's text elements to original
    container2.setTextElements(new java.util.ArrayList<>(list2));
    // container2.compareTo(container1) > 0, so container1's elements should be added at the front
    RectangularTextContainer<TextElement> result2 = container2.merge(container1);

    org.junit.Assert.assertSame(container2, result2);

    List<TextElement> mergedList2 = container2.getTextElements();
    org.junit.Assert.assertEquals(2, mergedList2.size());
    org.junit.Assert.assertEquals(te1, mergedList2.get(0));
    org.junit.Assert.assertEquals(te2, mergedList2.get(1));
}
