@Test
public void testSqueeze() {
    // Prepare TextElements with repeated characters 'a' and 'b'
    TextElement te1 = new TextElement("a");
    TextElement te2 = new TextElement("a");
    TextElement te3 = new TextElement("a");
    TextElement te4 = new TextElement("b");
    TextElement te5 = new TextElement("b");
    TextElement te6 = new TextElement("a");
    TextElement te7 = new TextElement("a");
    TextElement te8 = new TextElement("c");

    // Create a TextChunk from these elements
    List<TextElement> elements = List.of(te1, te2, te3, te4, te5, te6, te7, te8);
    TextChunk chunk = new TextChunk(elements);

    // Squeeze on character 'a' with minRunLength 2
    List<TextChunk> squeezed = chunk.squeeze('a', 2);

    // We expect the chunk to be split at runs of 'a' of length >= 2
    // The runs of 'a' are at indices 0-2 (length 3) and 5-6 (length 2)
    // So the squeeze should split into:
    // [chunk with te1, te2, te3], [chunk with te4, te5, te6, te7, te8] 
    // then recursively squeeze the second chunk on 'a' again, splitting at te6, te7
    // final expected chunks: 
    // 1) te1, te2, te3 (all 'a's)
    // 2) te4, te5 ('b's)
    // 3) te6, te7 ('a's)
    // 4) te8 ('c')

    // Check the number of chunks
    org.junit.Assert.assertEquals(4, squeezed.size());

    // Check texts of each chunk
    org.junit.Assert.assertEquals("aaa", squeezed.get(0).getText());
    org.junit.Assert.assertEquals("bb", squeezed.get(1).getText());
    org.junit.Assert.assertEquals("aa", squeezed.get(2).getText());
    org.junit.Assert.assertEquals("c", squeezed.get(3).getText());

    // Also test squeezing on character 'b' with minRunLength 2
    List<TextChunk> squeezedB = chunk.squeeze('b', 2);
    // The run of 'b' is te4, te5 (length 2)
    // So expected chunks:
    // 1) te1, te2, te3 ('a's)
    // 2) te4, te5 ('b's)
    // 3) te6, te7, te8 ('a', 'a', 'c')

    org.junit.Assert.assertEquals(3, squeezedB.size());
    org.junit.Assert.assertEquals("aaa", squeezedB.get(0).getText());
    org.junit.Assert.assertEquals("bb", squeezedB.get(1).getText());
    org.junit.Assert.assertEquals("aac", squeezedB.get(2).getText());

    // Test squeezing on character 'c' with minRunLength 1 (should isolate 'c')
    List<TextChunk> squeezedC = chunk.squeeze('c', 1);
    // 'c' appears once at the end, so it should be split out
    // Expected chunks:
    // 1) te1 to te7 (all except last)
    // 2) te8 ('c')

    org.junit.Assert.assertEquals(2, squeezedC.size());
    org.junit.Assert.assertEquals("aaaaaab", squeezedC.get(0).getText().replaceAll("\\s", ""));
    org.junit.Assert.assertEquals("c", squeezedC.get(1).getText());
}
