@Test
public void testParseNextUrl_Comprehensive() {
    // Arrange:
    String apiUrl = "https://api.example.com/resource";
    
    // Case 1: Valid nextUrl with query parameters
    Pagination pagination1 = new Pagination();
    pagination1.setNextUrl("https://api.example.com/resourceMethod?param1=value1&param2=value2");
    
    // Case 2: nextUrl is null or blank
    Pagination pagination2 = new Pagination();
    pagination2.setNextUrl("");
    
    // Case 3: nextUrl is malformed URL
    Pagination pagination3 = new Pagination();
    pagination3.setNextUrl("ht!tp://bad_url");
    
    // Act & Assert:
    // Case 1: Valid URL parsing
    Page page = PaginationHelper.parseNextUrl(pagination1, apiUrl);
    assertNotNull(page, "Page should not be null for valid nextUrl");
    
    // methodName should be the substring between apiUrl and "?" in the nextUrl, which is "Method"
    assertEquals("Method", page.getMethodName(), "Method name extracted incorrectly");
    
    // params should contain param1=value1 and param2=value2
    Map<String, String> params = page.getParams();
    assertNotNull(params, "Params map should not be null");
    assertEquals(2, params.size(), "Params map size mismatch");
    assertEquals("value1", params.get("param1"), "param1 value mismatch");
    assertEquals("value2", params.get("param2"), "param2 value mismatch");
    
    // Case 2: nextUrl is blank - expect OAuthException with message "No nextUrl"
    OAuthException ex1 = assertThrows(OAuthException.class, () -> {
        PaginationHelper.parseNextUrl(pagination2, apiUrl);
    });
    assertEquals("No nextUrl", ex1.getMessage());
    
    // Case 3: Malformed URL - expect OAuthException with message "Malformed URL"
    OAuthException ex2 = assertThrows(OAuthException.class, () -> {
        PaginationHelper.parseNextUrl(pagination3, apiUrl);
    });
    assertEquals("Malformed URL", ex2.getMessage());
}
