@Test
public void testMerge() {
    // Create first TextElement and TextChunk
    TextElement te1 = new TextElement();
    te1.x = 10f;
    te1.y = 20f;
    te1.width = 5f;
    te1.height = 7f;
    te1.text = "Hello";

    TextChunk chunk1 = new TextChunk(te1);

    // Create second TextElement and TextChunk
    TextElement te2 = new TextElement();
    te2.x = 12f;
    te2.y = 22f;
    te2.width = 6f;
    te2.height = 8f;
    te2.text = "World";

    TextChunk chunk2 = new TextChunk(te2);

    // Merge chunk2 into chunk1
    TextChunk merged = chunk1.merge(chunk2);

    // The returned object should be the same as chunk1
    org.junit.Assert.assertSame(chunk1, merged);

    // The merged chunk should contain text from both chunks
    String mergedText = merged.getText();
    org.junit.Assert.assertTrue(mergedText.contains("Hello"));
    org.junit.Assert.assertTrue(mergedText.contains("World"));

    // The merged chunk's bounding box should cover both chunks
    float expectedTop = Math.min(chunk1.getBounds2D().getY(), chunk2.getBounds2D().getY());
    float expectedLeft = Math.min(chunk1.getBounds2D().getX(), chunk2.getBounds2D().getX());
    float expectedBottom = Math.max(
            (float)(chunk1.getBounds2D().getY() + chunk1.getBounds2D().getHeight()),
            (float)(chunk2.getBounds2D().getY() + chunk2.getBounds2D().getHeight()));
    float expectedRight = Math.max(
            (float)(chunk1.getBounds2D().getX() + chunk1.getBounds2D().getWidth()),
            (float)(chunk2.getBounds2D().getX() + chunk2.getBounds2D().getWidth()));

    Rectangle2D bounds = merged.getBounds2D();
    org.junit.Assert.assertEquals(expectedTop, (float)bounds.getY(), 0.001);
    org.junit.Assert.assertEquals(expectedLeft, (float)bounds.getX(), 0.001);
    org.junit.Assert.assertEquals(expectedBottom - expectedTop, (float)bounds.getHeight(), 0.001);
    org.junit.Assert.assertEquals(expectedRight - expectedLeft, (float)bounds.getWidth(), 0.001);
}
