    // Act:
    Class<?>[] stringListTypes = Reflections.getGenericTypes(stringListField);
    Class<?>[] mapTypes = Reflections.getGenericTypes(mapField);
    Class<?>[] primitiveTypes = Reflections.getGenericTypes(primitiveField);
    Class<?>[] nonGenericTypes = Reflections.getGenericTypes(nonGenericField);

