@Test
public void signRequest_appendsAccessTokenAsQueryParameter() {
    // Arrange:
    InstagramApi api = new InstagramApi() {
        @Override
        public String getAccessTokenEndpoint() { return null; }
        @Override
        public Verb getAccessTokenVerb() { return null; }
        @Override
        public String getAuthorizationUrl(org.scribe.model.OAuthConfig config) { return null; }
        @Override
        public org.scribe.extractors.AccessTokenExtractor getAccessTokenExtractor() { return null; }
        @Override
        public org.scribe.oauth.OAuthService createService(org.scribe.model.OAuthConfig config) { return null; }
    };
    org.scribe.model.OAuthConfig config = new org.scribe.model.OAuthConfig("key", "secret", "callback");
    InstagramService service = new InstagramService(api, config);

    Token accessToken = new Token("myAccessTokenValue", "tokenSecret");
    OAuthRequest request = new OAuthRequest(Verb.GET, "https://api.instagram.com/v1/users/self");

    // Act:
    service.signRequest(accessToken, request);

    // Assert:
    // The request should contain the access token as a querystring parameter.
    String actualTokenParam = request.getQueryStringParams().stream()
            .filter(p -> OAuthConstants.ACCESS_TOKEN.equals(p.getKey()))
            .map(p -> p.getValue())
            .findFirst()
            .orElse(null);

    assertNotNull(actualTokenParam, "Access token query parameter should be present");
    assertEquals("myAccessTokenValue", actualTokenParam, "Access token value should match the token's token string");

    // Also verify that the request URL now includes the access token parameter properly encoded
    String completeUrl = request.getCompleteUrl();
    assertTrue(completeUrl.contains(OAuthConstants.ACCESS_TOKEN + "=" + OAuthEncoder.encode("myAccessTokenValue")),
            "Complete URL should contain the encoded access token as query parameter");
}
