@Test
public void testClip() {
    // Define clipping rectangle
    double xMin = 10, yMin = 10, xMax = 50, yMax = 50;
    Rectangle2D clipWindow = new Rectangle2D.Double(xMin, yMin, xMax - xMin, yMax - yMin);
    CohenSutherlandClipping clipper = new CohenSutherlandClipping(clipWindow);

    // 1. Line completely inside the clipping rectangle
    Line2D.Float lineInside = new Line2D.Float((float)(xMin + 1), (float)(yMin + 1), (float)(xMax - 1), (float)(yMax - 1));
    Line2D.Float lineInsideCopy = (Line2D.Float) lineInside.clone();
    boolean resultInside = clipper.clip(lineInside);
    // Should return true and line coordinates remain unchanged
    assertTrue(resultInside);
    assertEquals(lineInsideCopy.getX1(), lineInside.getX1(), 0.0001);
    assertEquals(lineInsideCopy.getY1(), lineInside.getY1(), 0.0001);
    assertEquals(lineInsideCopy.getX2(), lineInside.getX2(), 0.0001);
    assertEquals(lineInsideCopy.getY2(), lineInside.getY2(), 0.0001);

    // 2. Line completely outside and disjoint from the clipping rectangle
    Line2D.Float lineOutside = new Line2D.Float((float)(xMin - 10), (float)(yMin - 10), (float)(xMin - 5), (float)(yMin - 5));
    Line2D.Float lineOutsideCopy = (Line2D.Float) lineOutside.clone();
    boolean resultOutside = clipper.clip(lineOutside);
    // Should return false and line coordinates remain unchanged
    assertFalse(resultOutside);
    assertEquals(lineOutsideCopy.getX1(), lineOutside.getX1(), 0.0001);
    assertEquals(lineOutsideCopy.getY1(), lineOutside.getY1(), 0.0001);
    assertEquals(lineOutsideCopy.getX2(), lineOutside.getX2(), 0.0001);
    assertEquals(lineOutsideCopy.getY2(), lineOutside.getY2(), 0.0001);

    // 3. Line partially intersecting the clipping rectangle crossing multiple edges
    Line2D.Float linePartial = new Line2D.Float((float)(xMin - 5), (float)(yMin - 5), (float)(xMax + 5), (float)(yMax + 5));
    boolean resultPartial = clipper.clip(linePartial);
    // Should return true and line endpoints adjusted inside the rectangle
    assertTrue(resultPartial);
    assertTrue(linePartial.getX1() >= xMin - 0.0001 && linePartial.getX1() <= xMax + 0.0001);
    assertTrue(linePartial.getY1() >= yMin - 0.0001 && linePartial.getY1() <= yMax + 0.0001);
    assertTrue(linePartial.getX2() >= xMin - 0.0001 && linePartial.getX2() <= xMax + 0.0001);
    assertTrue(linePartial.getY2() >= yMin - 0.0001 && linePartial.getY2() <= yMax + 0.0001);

    // 4. Vertical line intersecting the clipping rectangle
    float xMid = (float)((xMin + xMax) / 2);
    Line2D.Float lineVertical = new Line2D.Float(xMid, (float)(yMin - 10), xMid, (float)(yMax + 10));
    boolean resultVertical = clipper.clip(lineVertical);
    // Should return true and line clipped to vertical segment inside rectangle
    assertTrue(resultVertical);
    assertEquals(xMid, lineVertical.getX1(), 0.0001);
    assertEquals(xMid, lineVertical.getX2(), 0.0001);
    assertEquals(yMin, lineVertical.getY1(), 0.0001);
    assertEquals(yMax, lineVertical.getY2(), 0.0001);

    // 5. Horizontal line intersecting the clipping rectangle
    float yMid = (float)((yMin + yMax) / 2);
    Line2D.Float lineHorizontal = new Line2D.Float((float)(xMin - 10), yMid, (float)(xMax + 10), yMid);
    boolean resultHorizontal = clipper.clip(lineHorizontal);
    // Should return true and line clipped to horizontal segment inside rectangle
    assertTrue(resultHorizontal);
    assertEquals(xMin, lineHorizontal.getX1(), 0.0001);
    assertEquals(xMax, lineHorizontal.getX2(), 0.0001);
    assertEquals(yMid, lineHorizontal.getY1(), 0.0001);
    assertEquals(yMid, lineHorizontal.getY2(), 0.0001);

    // 6. Line intersecting exactly at corner points
    // From corner to outside
    Line2D.Float lineCorner1 = new Line2D.Float((float)xMin, (float)yMin, (float)(xMin - 5), (float)(yMin - 5));
    boolean resultCorner1 = clipper.clip(lineCorner1);
    assertTrue(resultCorner1);
    // The clipped line should start at the corner and end inside or on the boundary (here clipped to corner)
    assertEquals(xMin, lineCorner1.getX1(), 0.0001);
    assertEquals(yMin, lineCorner1.getY1(), 0.0001);
    assertTrue(lineCorner1.getX2() >= xMin - 0.0001 && lineCorner1.getX2() <= xMax + 0.0001);
    assertTrue(lineCorner1.getY2() >= yMin - 0.0001 && lineCorner1.getY2() <= yMax + 0.0001);

    // From outside to corner
    Line2D.Float lineCorner2 = new Line2D.Float((float)(xMax + 5), (float)(yMax + 5), (float)xMax, (float)yMax);
    boolean resultCorner2 = clipper.clip(lineCorner2);
    assertTrue(resultCorner2);
    // The clipped line should end at the corner and start inside or on the boundary (here clipped to corner)
    assertTrue(lineCorner2.getX1() >= xMin - 0.0001 && lineCorner2.getX1() <= xMax + 0.0001);
    assertTrue(lineCorner2.getY1() >= yMin - 0.0001 && lineCorner2.getY1() <= yMax + 0.0001);
    assertEquals(xMax, lineCorner2.getX2(), 0.0001);
    assertEquals(yMax, lineCorner2.getY2(), 0.0001);
}
