public void testSortWithComparator() {
    // 1) Randomly ordered ArrayList<Integer>, natural ascending order
    List<Integer> arrayList = new java.util.ArrayList<>(java.util.Arrays.asList(5, 3, 8, 1, 9, 2));
    QuickSort.sort(arrayList, Comparator.naturalOrder());
    // Check sorted ascending
    for (int i = 1; i < arrayList.size(); i++) {
        assertTrue(arrayList.get(i - 1) <= arrayList.get(i));
    }

    // 2) LinkedList<String> sorted by string length ascending
    List<String> linkedList = new java.util.LinkedList<>(java.util.Arrays.asList("apple", "fig", "banana", "kiwi", "date"));
    QuickSort.sort(linkedList, Comparator.comparingInt(String::length));
    // Check sorted by length ascending
    for (int i = 1; i < linkedList.size(); i++) {
        assertTrue(linkedList.get(i - 1).length() <= linkedList.get(i).length());
    }

    // 3) Empty ArrayList<Double>
    List<Double> emptyList = new java.util.ArrayList<>();
    QuickSort.sort(emptyList, Comparator.naturalOrder());
    assertTrue(emptyList.isEmpty());

    // 4) Singleton LinkedList<Character>
    List<Character> singletonList = new java.util.LinkedList<>();
    singletonList.add('x');
    QuickSort.sort(singletonList, Comparator.naturalOrder());
    assertEquals(1, singletonList.size());
    assertEquals(Character.valueOf('x'), singletonList.get(0));

    // 5) LinkedList<Integer> with duplicates, sorted ascending
    List<Integer> duplicatesList = new java.util.LinkedList<>(java.util.Arrays.asList(4, 2, 4, 3, 2, 1, 1));
    QuickSort.sort(duplicatesList, Comparator.naturalOrder());
    for (int i = 1; i < duplicatesList.size(); i++) {
        assertTrue(duplicatesList.get(i - 1) <= duplicatesList.get(i));
    }
    // Check size unchanged
    assertEquals(7, duplicatesList.size());

    // 6) List with all equal elements
    List<String> equalElements = new java.util.ArrayList<>(java.util.Arrays.asList("same", "same", "same", "same"));
    QuickSort.sort(equalElements, Comparator.naturalOrder());
    // All elements remain equal and size unchanged
    assertEquals(4, equalElements.size());
    for (String s : equalElements) {
        assertEquals("same", s);
    }
}
