    @Test
    public void testHorizontalOverlap() {
        // (1) Full horizontal overlap where one rectangle lies completely within the other
        Rectangle r1 = new Rectangle(0, 1, 5, 5); // left=1, right=6
        Rectangle r2 = new Rectangle(0, 2, 3, 5); // left=2, right=5
        assertEquals(3.0f, r1.horizontalOverlap(r2), 0.0001f);

        // (2) Partial overlap at the edges
        r1 = new Rectangle(0, 1, 4, 5); // left=1, right=5
        r2 = new Rectangle(0, 4, 3, 5); // left=4, right=7
        assertEquals(1.0f, r1.horizontalOverlap(r2), 0.0001f);

        // (3) Touching edges without overlap
        r1 = new Rectangle(0, 1, 4, 5); // left=1, right=5
        r2 = new Rectangle(0, 5, 3, 5); // left=5, right=8
        assertEquals(0.0f, r1.horizontalOverlap(r2), 0.0001f);

        // (4) No overlap with a gap between rectangles
        r1 = new Rectangle(0, 1, 2, 5); // left=1, right=3
        r2 = new Rectangle(0, 4, 2, 5); // left=4, right=6
        assertEquals(0.0f, r1.horizontalOverlap(r2), 0.0001f);

        // (5) Identical rectangles
        r1 = new Rectangle(0, 2, 3, 5); // left=2, right=5
        r2 = new Rectangle(0, 2, 3, 5); // left=2, right=5
        assertEquals(3.0f, r1.horizontalOverlap(r2), 0.0001f);

        // Zero width rectangle overlapping with normal rectangle
        r1 = new Rectangle(0, 2, 0, 5); // left=2, right=2
        r2 = new Rectangle(0, 1, 3, 5); // left=1, right=4
        assertEquals(0.0f, r1.horizontalOverlap(r2), 0.0001f);

        // Normal rectangle overlapping with zero width rectangle
        r1 = new Rectangle(0, 1, 3, 5); // left=1, right=4
        r2 = new Rectangle(0, 4, 0, 5); // left=4, right=4
        assertEquals(0.0f, r1.horizontalOverlap(r2), 0.0001f);

        // Rectangles with reversed order (other overlaps this)
        r1 = new Rectangle(0, 4, 3, 5); // left=4, right=7
        r2 = new Rectangle(0, 1, 4, 5); // left=1, right=5
        assertEquals(1.0f, r1.horizontalOverlap(r2), 0.0001f);
    }
