    @Test
    public void testOverlapRatio() {
        // Identical rectangles (full overlap)
        Rectangle r1 = new Rectangle(0, 0, 10, 10);
        Rectangle r2 = new Rectangle(0, 0, 10, 10);
        // intersection = 100, union = 100, ratio = 1.0
        assertEquals(1.0f, r1.overlapRatio(r2), DELTA);

        // One rectangle entirely inside another
        Rectangle outer = new Rectangle(0, 0, 10, 10);
        Rectangle inner = new Rectangle(2, 2, 5, 5);
        // intersection = 25, union = 100, ratio = 25/100 = 0.25
        assertEquals(25f / 100f, outer.overlapRatio(inner), DELTA);
        assertEquals(25f / 100f, inner.overlapRatio(outer), DELTA);

        // Partially overlapping rectangles
        Rectangle p1 = new Rectangle(0, 0, 10, 10);
        Rectangle p2 = new Rectangle(5, 5, 10, 10);
        // intersection area = 5*5=25
        // union area = 100 + 100 - 25 = 175
        // ratio = 25/175 = 0.14285714
        assertEquals(25f / 175f, p1.overlapRatio(p2), DELTA);
        assertEquals(25f / 175f, p2.overlapRatio(p1), DELTA);

        // Rectangles touching at edges (no area overlap)
        Rectangle edge1 = new Rectangle(0, 0, 10, 10);
        Rectangle edge2 = new Rectangle(10, 0, 10, 10);
        // intersection area = 0, ratio = 0
        assertEquals(0f, edge1.overlapRatio(edge2), DELTA);
        assertEquals(0f, edge2.overlapRatio(edge1), DELTA);

        // Rectangles touching at corners (no area overlap)
        Rectangle corner1 = new Rectangle(0, 0, 10, 10);
        Rectangle corner2 = new Rectangle(10, 10, 10, 10);
        // intersection area = 0, ratio = 0
        assertEquals(0f, corner1.overlapRatio(corner2), DELTA);
        assertEquals(0f, corner2.overlapRatio(corner1), DELTA);

        // Completely separate rectangles
        Rectangle sep1 = new Rectangle(0, 0, 10, 10);
        Rectangle sep2 = new Rectangle(20, 20, 5, 5);
        // intersection area = 0, ratio = 0
        assertEquals(0f, sep1.overlapRatio(sep2), DELTA);
        assertEquals(0f, sep2.overlapRatio(sep1), DELTA);

        // Degenerate rectangles with zero width and/or height
        Rectangle zeroWidth = new Rectangle(0, 0, 0, 10);
        Rectangle zeroHeight = new Rectangle(0, 0, 10, 0);
        Rectangle normal = new Rectangle(0, 0, 10, 10);

        // zero width rectangle overlap with normal
        assertEquals(0f, zeroWidth.overlapRatio(normal), DELTA);
        assertEquals(0f, normal.overlapRatio(zeroWidth), DELTA);

        // zero height rectangle overlap with normal
        assertEquals(0f, zeroHeight.overlapRatio(normal), DELTA);
        assertEquals(0f, normal.overlapRatio(zeroHeight), DELTA);

        // zero width and zero height overlap
        assertEquals(0f, zeroWidth.overlapRatio(zeroHeight), DELTA);
        assertEquals(0f, zeroHeight.overlapRatio(zeroWidth), DELTA);
    }
