@Test
public void testSetTop() {
    // Initialize rectangle with top=10, left=5, width=15, height=20
    Rectangle rect = new Rectangle(10, 5, 15, 20);
    float originalBottom = rect.getTop() + rect.getHeight(); // 10 + 20 = 30
    float originalLeft = rect.getLeft();
    float originalWidth = rect.getWidth();

    // Move top edge downward to 15
    rect.setTop(15);
    // Verify top is now 15
    assertEquals(15f, rect.getTop(), 0.0001);
    // Verify height decreased to 15 (30 - 15)
    assertEquals(15f, rect.getHeight(), 0.0001);
    // Verify bottom edge remains fixed at 30
    assertEquals(originalBottom, rect.getTop() + rect.getHeight(), 0.0001);
    // Verify left and width remain unchanged
    assertEquals(originalLeft, rect.getLeft(), 0.0001);
    assertEquals(originalWidth, rect.getWidth(), 0.0001);

    // Move top edge upward to 5
    rect.setTop(5);
    // Verify top is now 5
    assertEquals(5f, rect.getTop(), 0.0001);
    // Verify height increased to 25 (30 - 5)
    assertEquals(25f, rect.getHeight(), 0.0001);
    // Verify bottom edge remains fixed at 30
    assertEquals(originalBottom, rect.getTop() + rect.getHeight(), 0.0001);
    // Verify left and width remain unchanged
    assertEquals(originalLeft, rect.getLeft(), 0.0001);
    assertEquals(originalWidth, rect.getWidth(), 0.0001);

    // Set top equal to current top (5) - no change expected
    float currentTop = rect.getTop();
    float currentHeight = rect.getHeight();
    rect.setTop(currentTop);
    assertEquals(currentTop, rect.getTop(), 0.0001);
    assertEquals(currentHeight, rect.getHeight(), 0.0001);
    assertEquals(originalBottom, rect.getTop() + rect.getHeight(), 0.0001);

    // Test setting top to bottom edge (30) - height should become zero
    rect.setTop(originalBottom);
    assertEquals(originalBottom, rect.getTop(), 0.0001);
    assertEquals(0f, rect.getHeight(), 0.0001);
    assertEquals(originalBottom, rect.getTop() + rect.getHeight(), 0.0001);

    // Test setting top greater than bottom edge (e.g., 35) - height becomes negative
    rect.setTop(originalBottom + 5);
    assertEquals(originalBottom + 5, rect.getTop(), 0.0001);
    assertEquals(-5f, rect.getHeight(), 0.0001);
    assertEquals(originalBottom, rect.getTop() + rect.getHeight(), 0.0001);
}
