    @Test
    public void testGetTextElements() {
        ConcreteRectangularTextContainer container = new ConcreteRectangularTextContainer(0, 0, 100, 100);

        // Prepare known TextChunk elements
        TextChunk chunk1 = new TextChunk("Hello", null, 0, 0, 0, 0);
        TextChunk chunk2 = new TextChunk("World", null, 0, 0, 0, 0);
        TextChunk chunk3 = new TextChunk("Test", null, 0, 0, 0, 0);

        List<TextChunk> inputList = new ArrayList<>();
        inputList.add(chunk1);
        inputList.add(chunk2);
        inputList.add(chunk3);

        // Set the textElements list
        container.setTextElements(inputList);

        // Retrieve the list via getTextElements()
        List<TextChunk> returnedList = container.getTextElements();

        // Verify the returned list contains exactly the same elements in the same order
        assertEquals("Returned list size should match input list size", inputList.size(), returnedList.size());
        for (int i = 0; i < inputList.size(); i++) {
            assertSame("Element at index " + i + " should be the same instance", inputList.get(i), returnedList.get(i));
        }

        // Test if modifications to the returned list affect the internal list
        // Add an element to the returned list
        TextChunk chunk4 = new TextChunk("NewElement", null, 0, 0, 0, 0);
        returnedList.add(chunk4);

        // The internal list should reflect this addition since getTextElements() returns the internal list directly
        List<TextChunk> internalListAfterAdd = container.getTextElements();
        assertTrue("Internal list should contain the newly added element", internalListAfterAdd.contains(chunk4));

        // Remove an element from the returned list
        returnedList.remove(chunk1);
        List<TextChunk> internalListAfterRemove = container.getTextElements();
        assertFalse("Internal list should no longer contain the removed element", internalListAfterRemove.contains(chunk1));
    }
