@Test
public void testSetTextElements() {
    // Create a RectangularTextContainer instance with dummy dimensions
    RectangularTextContainer<TextChunk> container = new RectangularTextContainer<TextChunk>(0, 0, 100, 100) {};

    // Prepare a non-empty list of TextChunk elements
    TextChunk chunk1 = new TextChunk("Hello", 0, 0, 10, 10);
    TextChunk chunk2 = new TextChunk("World", 10, 0, 20, 10);
    List<TextChunk> nonEmptyList = List.of(chunk1, chunk2);

    // Set the non-empty list and verify
    container.setTextElements(nonEmptyList);
    List<TextChunk> retrieved = container.getTextElements();
    // The internal list should be exactly the same instance as the input list
    assertSame(nonEmptyList, retrieved);
    // The contents should be equal and in the same order
    assertEquals(2, retrieved.size());
    assertEquals(chunk1, retrieved.get(0));
    assertEquals(chunk2, retrieved.get(1));

    // Prepare an empty list
    List<TextChunk> emptyList = List.of();

    // Set the empty list and verify
    container.setTextElements(emptyList);
    retrieved = container.getTextElements();
    assertSame(emptyList, retrieved);
    assertTrue(retrieved.isEmpty());

    // Test null input behavior
    // According to the method, null is allowed and sets the internal field to null
    container.setTextElements(null);
    retrieved = container.getTextElements();
    assertNull(retrieved);
}
