@Test
public void testToString_appendsTextFieldCorrectly() {
    // Helper subclass to allow setting text directly since getText/setText are public
    class TestRectangularTextContainer extends RectangularTextContainer<TextElement> {
        private String text;

        public TestRectangularTextContainer(String text) {
            super(0, 0, 0, 0);
            this.text = text;
        }

        @Override
        public String getText() {
            return text;
        }

        @Override
        public String toString() {
            return super.toString();
        }
    }

    // Create instances with different text values
    TestRectangularTextContainer rtcNull = new TestRectangularTextContainer(null);
    TestRectangularTextContainer rtcEmpty = new TestRectangularTextContainer("");
    TestRectangularTextContainer rtcHello = new TestRectangularTextContainer("Hello World");
    TestRectangularTextContainer rtcSpecial = new TestRectangularTextContainer("Line\nBreak\tTabbed");

    // Extract superclass toString prefix (without last ']')
    String superPrefixNull = rtcNull.toString().substring(0, rtcNull.toString().lastIndexOf(",text="));
    String superPrefixEmpty = rtcEmpty.toString().substring(0, rtcEmpty.toString().lastIndexOf(",text="));
    String superPrefixHello = rtcHello.toString().substring(0, rtcHello.toString().lastIndexOf(",text="));
    String superPrefixSpecial = rtcSpecial.toString().substring(0, rtcSpecial.toString().lastIndexOf(",text="));

    // Because the superclass toString is unknown, we verify the prefix is consistent for all instances
    // and that the appended text field matches expected format.

    // Check null text
    String toStringNull = rtcNull.toString();
    // Should end with ,text=null]
    org.junit.Assert.assertTrue(toStringNull.endsWith(",text=null]"));
    // The prefix before ,text= should be the superclass string minus last ']'
    org.junit.Assert.assertEquals(toStringNull.substring(0, toStringNull.lastIndexOf(",text=")), superPrefixNull);

    // Check empty string text
    String toStringEmpty = rtcEmpty.toString();
    org.junit.Assert.assertTrue(toStringEmpty.endsWith(",text=\"\"]"));
    org.junit.Assert.assertEquals(toStringEmpty.substring(0, toStringEmpty.lastIndexOf(",text=")), superPrefixEmpty);

    // Check "Hello World" text
    String toStringHello = rtcHello.toString();
    org.junit.Assert.assertTrue(toStringHello.endsWith(",text=\"Hello World\"]"));
    org.junit.Assert.assertEquals(toStringHello.substring(0, toStringHello.lastIndexOf(",text=")), superPrefixHello);

    // Check special characters text
    String toStringSpecial = rtcSpecial.toString();
    // The string should end with ,text="Line\nBreak\tTabbed"]
    // Because the actual string contains real newline and tab characters, we check the substring after ,text= is exactly quoted text
    String expectedSuffixSpecial = ",text=\"Line\nBreak\tTabbed\"]";
    org.junit.Assert.assertTrue(toStringSpecial.endsWith(expectedSuffixSpecial));
    org.junit.Assert.assertEquals(toStringSpecial.substring(0, toStringSpecial.lastIndexOf(",text=")), superPrefixSpecial);
}
