    @Test
    public void testSplitAt_ValidIndex() {
        List<TextElement> elements = new ArrayList<>();
        elements.add(createTextElement("A", 0, 0));
        elements.add(createTextElement("B", 1, 0));
        elements.add(createTextElement("C", 2, 0));
        elements.add(createTextElement("D", 3, 0));
        elements.add(createTextElement("E", 4, 0));

        TextChunk chunk = new TextChunk(elements);

        // Split at i=2
        TextChunk[] split = chunk.splitAt(2);
        assertEquals(2, split.length);

        // First chunk should contain elements 0 and 1
        String firstText = split[0].getText(false);
        assertTrue(firstText.contains("A"));
        assertTrue(firstText.contains("B"));
        assertFalse(firstText.contains("C"));
        assertFalse(firstText.contains("D"));
        assertFalse(firstText.contains("E"));

        // Second chunk should contain elements 2,3,4
        String secondText = split[1].getText(false);
        assertFalse(secondText.contains("A"));
        assertFalse(secondText.contains("B"));
        assertTrue(secondText.contains("C"));
        assertTrue(secondText.contains("D"));
        assertTrue(secondText.contains("E"));
    }
