    @Test
    public void testSetRight() {
        // Initial rectangle: x=10, y=20, width=30, height=40
        Rectangle rect = new Rectangle(20, 10, 30, 40);

        // Call setRight(50)
        rect.setRight(50);
        // x should remain 10
        assertEquals(10f, rect.getLeft(), 0.0001f);
        // y should remain 20
        assertEquals(20f, rect.getTop(), 0.0001f);
        // height should remain 40
        assertEquals(40f, rect.getBottom() - rect.getTop(), 0.0001f);
        // width should update to 40 (50 - 10)
        assertEquals(40f, rect.getRight() - rect.getLeft(), 0.0001f);

        // Test case where right equals x (width should become zero)
        rect = new Rectangle(20, 10, 30, 40);
        rect.setRight(10);
        assertEquals(10f, rect.getLeft(), 0.0001f);
        assertEquals(20f, rect.getTop(), 0.0001f);
        assertEquals(0f, rect.getRight() - rect.getLeft(), 0.0001f);
        assertEquals(40f, rect.getBottom() - rect.getTop(), 0.0001f);

        // Test case where right < x (width becomes negative)
        rect = new Rectangle(20, 10, 30, 40);
        rect.setRight(5);
        assertEquals(10f, rect.getLeft(), 0.0001f);
        assertEquals(20f, rect.getTop(), 0.0001f);
        assertEquals(-5f, rect.getRight() - rect.getLeft(), 0.0001f);
        assertEquals(40f, rect.getBottom() - rect.getTop(), 0.0001f);

        // Test with floating-point right value
        rect = new Rectangle(20, 10, 30, 40);
        rect.setRight(40.5f);
        assertEquals(10f, rect.getLeft(), 0.0001f);
        assertEquals(20f, rect.getTop(), 0.0001f);
        assertEquals(30.5f, rect.getRight() - rect.getLeft(), 0.0001f);
        assertEquals(40f, rect.getBottom() - rect.getTop(), 0.0001f);
    }
