    assertFalse("Touching edges without overlap should return false", r3.verticallyOverlaps(r4));
    assertFalse("Symmetric check", r4.verticallyOverlaps(r3));

    // (3) completely non-overlapping with one rectangle above the other
    Rectangle r5 = new Rectangle(1, 0, 1, 2); // vertical span: 1 to 3
    Rectangle r6 = new Rectangle(4, 0, 1, 2); // vertical span: 4 to 6
    assertFalse("Non-overlapping rectangles should return false", r5.verticallyOverlaps(r6));
    assertFalse("Symmetric check", r6.verticallyOverlaps(r5));

