    @Test
    public void testHorizontallyOverlaps() {
        // Partial horizontal overlap
        Rectangle r1 = new Rectangle(0, 2, 5, 10); // left=2, width=5
        Rectangle r2 = new Rectangle(0, 5, 4, 10); // left=5, width=4
        assertTrue("Partial overlap should return true", r1.horizontallyOverlaps(r2));
        assertTrue("Partial overlap should be symmetric", r2.horizontallyOverlaps(r1));

        // One rectangle completely inside the other horizontally
        Rectangle r3 = new Rectangle(0, 1, 10, 10); // left=1, width=10
        Rectangle r4 = new Rectangle(0, 3, 4, 10);  // left=3, width=4
        assertTrue("Full containment should return true", r3.horizontallyOverlaps(r4));
        assertTrue("Full containment should be symmetric", r4.horizontallyOverlaps(r3));

        // Rectangles touching exactly at one edge without overlapping
        Rectangle r5 = new Rectangle(0, 0, 5, 10); // left=0, width=5
        Rectangle r6 = new Rectangle(0, 5, 3, 10); // left=5, width=3
        assertFalse("Touching edges should return false", r5.horizontallyOverlaps(r6));
        assertFalse("Touching edges should be symmetric", r6.horizontallyOverlaps(r5));

        // Rectangles completely separated horizontally
        Rectangle r7 = new Rectangle(0, 0, 4, 10); // left=0, width=4
        Rectangle r8 = new Rectangle(0, 6, 2, 10); // left=6, width=2
        assertFalse("Separated rectangles should return false", r7.horizontallyOverlaps(r8));
        assertFalse("Separated rectangles should be symmetric", r8.horizontallyOverlaps(r7));

        // Rectangles with identical horizontal bounds
        Rectangle r9 = new Rectangle(0, 2, 5, 10); // left=2, width=5
        Rectangle r10 = new Rectangle(0, 2, 5, 10); // left=2, width=5
        assertTrue("Identical bounds should return true", r9.horizontallyOverlaps(r10));
        assertTrue("Identical bounds should be symmetric", r10.horizontallyOverlaps(r9));
    }
