    @Test
    public void testMerge() {
        // rect1: left=1, top=1, width=4, height=3
        Rectangle rect1 = new Rectangle(1, 1, 4, 3);
        // rect2: left=3, top=2, width=5, height=4
        Rectangle rect2 = new Rectangle(2, 3, 5, 4);

        // Merge rect2 into rect1
        Rectangle returned = rect1.merge(rect2);

        // After merge, rect1 should be the minimal bounding rectangle of both
        // left = min(1,3) = 1
        // top = min(1,2) = 1
        // right = max(1+4, 3+5) = max(5,8) = 8
        // bottom = max(1+3, 2+4) = max(4,6) = 6
        // width = right - left = 8 - 1 = 7
        // height = bottom - top = 6 - 1 = 5

        assertSame("merge should return the same instance", rect1, returned);
        assertEquals(1f, rect1.getLeft(), 0.0001);
        assertEquals(1f, rect1.getTop(), 0.0001);
        assertEquals(7f, rect1.getRight() - rect1.getLeft(), 0.0001);
        assertEquals(5f, rect1.getBottom() - rect1.getTop(), 0.0001);

        // Test merging when other is fully inside rect1 (no enlargement)
        Rectangle rect3 = new Rectangle(1.5f, 1.5f, 2, 1);
        Rectangle beforeMerge = new Rectangle(rect1.getTop(), rect1.getLeft(),
                rect1.getRight() - rect1.getLeft(), rect1.getBottom() - rect1.getTop());
        Rectangle returned2 = rect1.merge(rect3);
        assertSame("merge should return the same instance", rect1, returned2);
        // rect1 bounds should remain unchanged
        assertEquals(beforeMerge.getLeft(), rect1.getLeft(), 0.0001);
        assertEquals(beforeMerge.getTop(), rect1.getTop(), 0.0001);
        assertEquals(beforeMerge.getRight(), rect1.getRight(), 0.0001);
        assertEquals(beforeMerge.getBottom(), rect1.getBottom(), 0.0001);

        // Test merging two non-overlapping rectangles
        Rectangle rect4 = new Rectangle(10, 10, 2, 2);
        Rectangle rect5 = new Rectangle(13, 13, 3, 3);
        Rectangle rect4Copy = new Rectangle(rect4.getTop(), rect4.getLeft(),
                rect4.getRight() - rect4.getLeft(), rect4.getBottom() - rect4.getTop());
        Rectangle returned3 = rect4.merge(rect5);
        assertSame("merge should return the same instance", rect4, returned3);
        // left = min(10,13) = 10
        // top = min(10,13) = 10
        // right = max(12,16) = 16
        // bottom = max(12,16) = 16
        assertEquals(10f, rect4.getLeft(), 0.0001);
        assertEquals(10f, rect4.getTop(), 0.0001);
        assertEquals(16f, rect4.getRight(), 0.0001);
        assertEquals(16f, rect4.getBottom(), 0.0001);
        assertEquals(6f, rect4.getRight() - rect4.getLeft(), 0.0001);
        assertEquals(6f, rect4.getBottom() - rect4.getTop(), 0.0001);

        // Test merging rectangles that share edges
        Rectangle rect6 = new Rectangle(0, 0, 4, 4);
        Rectangle rect7 = new Rectangle(4, 0, 4, 4); // directly below rect6, shares edge at y=4
        Rectangle returned4 = rect6.merge(rect7);
        assertSame("merge should return the same instance", rect6, returned4);
        // left = min(0,0) = 0
        // top = min(0,4) = 0
        // right = max(4,4) = 4
        // bottom = max(4,8) = 8
        assertEquals(0f, rect6.getLeft(), 0.0001);
        assertEquals(0f, rect6.getTop(), 0.0001);
        assertEquals(4f, rect6.getRight(), 0.0001);
        assertEquals(8f, rect6.getBottom(), 0.0001);
        assertEquals(4f, rect6.getRight() - rect6.getLeft(), 0.0001);
        assertEquals(8f, rect6.getBottom() - rect6.getTop(), 0.0001);
    }
