    @Test
    public void testSetBottom() {
        // Create a Rectangle with known coordinates and size
        float top = 10f;
        float left = 5f;
        float width = 20f;
        float height = 15f;
        Rectangle rect = new Rectangle(top, left, width, height);

        // Initial bottom should be top + height = 10 + 15 = 25
        float initialBottom = rect.getBottom();
        assertEquals(25f, initialBottom, 0.0001f);

        // 1) Call setBottom with a value greater than current bottom (e.g., 30)
        float newBottom = 30f;
        rect.setBottom(newBottom);

        // Height should become newBottom - top = 30 - 10 = 20
        assertEquals(top, rect.getTop(), 0.0001f);
        assertEquals(left, rect.getLeft(), 0.0001f);
        assertEquals(width, rect.getRight() - rect.getLeft(), 0.0001f);
        assertEquals(newBottom, rect.getBottom(), 0.0001f);
        assertEquals(20f, rect.getBottom() - rect.getTop(), 0.0001f);

        // 2) Call setBottom with a value equal to current bottom (30)
        rect.setBottom(newBottom);
        // No change expected
        assertEquals(top, rect.getTop(), 0.0001f);
        assertEquals(left, rect.getLeft(), 0.0001f);
        assertEquals(width, rect.getRight() - rect.getLeft(), 0.0001f);
        assertEquals(newBottom, rect.getBottom(), 0.0001f);

        // 3) Call setBottom with a value less than top (e.g., 5)
        float bottomLessThanTop = 5f;
        rect.setBottom(bottomLessThanTop);

        // Height should be bottomLessThanTop - top = 5 - 10 = -5 (negative)
        assertEquals(top, rect.getTop(), 0.0001f);
        assertEquals(left, rect.getLeft(), 0.0001f);
        assertEquals(width, rect.getRight() - rect.getLeft(), 0.0001f);
        assertEquals(bottomLessThanTop, rect.getBottom(), 0.0001f);
        assertEquals(-5f, rect.getBottom() - rect.getTop(), 0.0001f);
    }
