    @Test
    public void testVerticalOverlap() {
        // (1) No vertical overlap - one rectangle entirely above the other
        Rectangle r1 = new Rectangle(0, 0, 10, 10);   // top=0, bottom=10
        Rectangle r2 = new Rectangle(15, 0, 10, 5);   // top=15, bottom=20
        assertEquals(0f, r1.verticalOverlap(r2), 0.0001f);
        assertEquals(0f, r2.verticalOverlap(r1), 0.0001f);

        // (2) Partial vertical overlap
        Rectangle r3 = new Rectangle(0, 0, 10, 10);   // top=0, bottom=10
        Rectangle r4 = new Rectangle(5, 0, 10, 10);   // top=5, bottom=15
        // Overlap from 5 to 10 = 5
        assertEquals(5f, r3.verticalOverlap(r4), 0.0001f);
        assertEquals(5f, r4.verticalOverlap(r3), 0.0001f);

        // (3) Full vertical overlap - one rectangle inside the other
        Rectangle r5 = new Rectangle(0, 0, 10, 20);   // top=0, bottom=20
        Rectangle r6 = new Rectangle(5, 0, 10, 5);    // top=5, bottom=10
        // Overlap is height of smaller rectangle = 5
        assertEquals(5f, r5.verticalOverlap(r6), 0.0001f);
        assertEquals(5f, r6.verticalOverlap(r5), 0.0001f);

        // (4) Edge touching - bottom of one equals top of the other
        Rectangle r7 = new Rectangle(0, 0, 10, 10);   // top=0, bottom=10
        Rectangle r8 = new Rectangle(10, 0, 10, 5);   // top=10, bottom=15
        // Touching edges, no overlap
        assertEquals(0f, r7.verticalOverlap(r8), 0.0001f);
        assertEquals(0f, r8.verticalOverlap(r7), 0.0001f);
    }
