@Test
public void testGetField() throws NoSuchFieldException {
    // Arrange:
    class SuperClass {
        private int superField;
    }
    class SubClass extends SuperClass {
        private String subField;
    }

    // Act & Assert:

    // 1. Field declared in subclass
    Field subField = Reflections.getField(SubClass.class, "subField");
    assertNotNull(subField);
    assertEquals("subField", subField.getName());
    assertEquals(SubClass.class, subField.getDeclaringClass());

    // 2. Field declared in superclass
    Field superField = Reflections.getField(SubClass.class, "superField");
    assertNotNull(superField);
    assertEquals("superField", superField.getName());
    assertEquals(SuperClass.class, superField.getDeclaringClass());

    // 3. Non-existent field returns null (search reaches Object)
    Field noField = Reflections.getField(SubClass.class, "noSuchField");
    assertNull(noField);

    // 4. Directly searching Object class returns null
    Field objectField = Reflections.getField(Object.class, "anyField");
    assertNull(objectField);
}
