        ValueFormatter<Object> localDateFormatter = zapposJson.getValueFormatter(LocalDate.class);
        ValueFormatter<Object> instantFormatter = zapposJson.getValueFormatter(Instant.class);

        // Act for a class not registered:
        class UnregisteredClass {}
        ValueFormatter<Object> nullFormatter = zapposJson.getValueFormatter(UnregisteredClass.class);

        // Assert:
        // The returned formatters should not be null for registered classes
        assertNotNull(dateFormatter, "Date formatter should not be null");
        assertNotNull(sqlDateFormatter, "SQL Date formatter should not be null");
        assertNotNull(timestampFormatter, "Timestamp formatter should not be null");
        assertNotNull(bigIntegerFormatter, "BigInteger formatter should not be null");
