        assertEquals(newPattern, internalFormatter.toString().split("\\[")[0], "Formatter pattern should be updated");

        // Check that zone is system default
        assertEquals(ZoneId.systemDefault(), internalFormatter.getZone(), "Formatter zone should be system default");

        // Additionally, verify the formatter formats a LocalDate correctly according to the new pattern
        LocalDate date = LocalDate.of(2024, 6, 15);
        String formattedDate = internalFormatter.format(date.atStartOfDay(ZoneId.systemDefault()));
        String expected = String.format("%02d/%02d/%04d", date.getDayOfMonth(), date.getMonthValue(), date.getYear());
        assertEquals(expected, formattedDate, "Formatter should format date according to the new pattern");
