@Test
public void testCropRulingsToArea() {
    // Create a rectangular area to crop to
    Rectangle2D area = new Rectangle2D.Float(10, 10, 30, 30);

    // Create rulings: some fully inside, some partially inside, some outside
    List<Ruling> rulings = new LinkedList<>();

    // Fully inside
    rulings.add(new Ruling(15, 15, 10, 0)); // horizontal line inside area
    rulings.add(new Ruling(20, 20, 0, 5));  // vertical line inside area

    // Partially inside (crossing boundary)
    rulings.add(new Ruling(5, 5, 20, 0));   // horizontal line partially inside (from 5,5 to 25,5)
    rulings.add(new Ruling(35, 25, 0, 20)); // vertical line partially inside (from 25,35 to 25,55)

    // Fully outside
    rulings.add(new Ruling(0, 0, 5, 0));    // horizontal line outside
    rulings.add(new Ruling(50, 50, 0, 10)); // vertical line outside

    List<Ruling> cropped = Ruling.cropRulingsToArea(rulings, area);

    // We expect only the lines that intersect the area to be returned
    // That is: first 4 rulings (2 fully inside + 2 partially inside)
    // The last 2 are fully outside and should be excluded
    // So cropped.size() == 4
    org.junit.Assert.assertEquals(4, cropped.size());

    // Check that each cropped ruling lies within the area bounds
    for (Ruling r : cropped) {
        // The intersect method should have trimmed the ruling to be inside the area
        org.junit.Assert.assertTrue(area.contains(r.getLeft(), r.getTop()));
        org.junit.Assert.assertTrue(area.contains(r.getRight(), r.getBottom()));
    }
}
