@Test
public void testAddAttributeInfo_AddsAttributeInfoToListAndReturnsIt() {
    // Arrange:
    Class<?> typeInfoClass = String.class;
    TypeInfo typeInfo = new TypeInfo(typeInfoClass);

    Class<?> attrType = Integer.class;
    JsonBeanAttribute jsonBeanAttribute = new JsonBeanAttribute();

    // Act:
    AttributeInfo result = typeInfo.addAttributeInfo(attrType, jsonBeanAttribute);

    // Assert:
    // 1. Returned AttributeInfo is not null
    assertNotNull(result, "Returned AttributeInfo should not be null");

    // 2. Returned AttributeInfo has correct type and detail
    assertEquals(attrType, result.getType(), "AttributeInfo type should be as provided");
    assertEquals(jsonBeanAttribute, result.getDetail(), "AttributeInfo detail should be as provided");

    // 3. The internal list contains the returned AttributeInfo instance
    List<AttributeInfo> attrInfos = typeInfo.getAttrInfos();
    assertNotNull(attrInfos, "Internal attribute info list should not be null");
    assertEquals(1, attrInfos.size(), "Internal attribute info list size should be 1");
    assertSame(result, attrInfos.get(0), "The attribute info in the list should be the same as the returned instance");

    // 4. Adding a second AttributeInfo works as expected
    Class<?> secondAttrType = Double.class;
    JsonBeanAttribute secondJsonBeanAttribute = new JsonBeanAttribute();

    AttributeInfo secondResult = typeInfo.addAttributeInfo(secondAttrType, secondJsonBeanAttribute);

    assertNotNull(secondResult, "Second returned AttributeInfo should not be null");
    assertEquals(secondAttrType, secondResult.getType(), "Second AttributeInfo type should be as provided");
    assertEquals(secondJsonBeanAttribute, secondResult.getDetail(), "Second AttributeInfo detail should be as provided");

    List<AttributeInfo> updatedAttrInfos = typeInfo.getAttrInfos();
    assertEquals(2, updatedAttrInfos.size(), "Internal attribute info list size should be 2 after adding second attribute");
    assertSame(secondResult, updatedAttrInfos.get(1), "The second attribute info in the list should be the same as the second returned instance");
}
