@Test
public void testWriteIterable_SerializesIterableAsJsonArray() throws IOException {
    // Arrange:
    ZapposJson mockZapposJson = mock(ZapposJson.class);
    StringWriter writer = new StringWriter();
    List<String> iterable = Arrays.asList("apple", "banana", "cherry");

    // We simulate that zapposJson.toJson writes the string surrounded by double quotes:
    doAnswer(invocation -> {
        Object arg = invocation.getArgument(0);
        Writer w = invocation.getArgument(1);
        w.write("\"" + arg.toString() + "\"");
        return null;
    }).when(mockZapposJson).toJson(any(), eq(writer));

    // Act:
    JsonWriter.writeIterable(mockZapposJson, iterable, writer);

    // Assert:
    // The expected JSON array: ["apple","banana","cherry"]
    String expectedJson = "[\"apple\",\"banana\",\"cherry\"]";
    assertEquals(expectedJson, writer.toString());

    // Also verify that toJson was called exactly three times, once per element
    verify(mockZapposJson, times(3)).toJson(any(), eq(writer));
}
