    @Test
    public void testFormat_withPatternAndWithoutPattern() {
        class PatternHolder {
            String pattern;
        }
        final PatternHolder patternHolder = new PatternHolder();
        BigDecimalFormatter formatter = new BigDecimalFormatter() {
            @Override
            public String getPattern() {
                return patternHolder.pattern;
            }
            @Override
            public String toJsonValue(ZapposJson zapposJson, String value) {
                return "\"" + value + "\"";
            }
        };

        ZapposJson zapposJson = ZapposJson.getInstance();

        BigDecimal value = new BigDecimal("1234.5678");
        patternHolder.pattern = "#,##0.00";
        String formattedWithPattern = formatter.format(zapposJson, value);
        assertEquals("\"1,234.57\"", formattedWithPattern);
        patternHolder.pattern = null;
        String formattedWithoutPattern = formatter.format(zapposJson, value);
        assertEquals(value.toString(), formattedWithoutPattern);
    }
