@Test
public void testParse_withPatternAndWithoutPattern() throws Exception {
    // Arrange:
    JavaDateFormatter formatterWithPattern = new JavaDateFormatter() {
        @Override
        public String getPattern() {
            return "yyyy-MM-dd";
        }
    };
    JavaDateFormatter formatterWithoutPattern = new JavaDateFormatter() {
        @Override
        public String getPattern() {
            return null;
        }
    };
    String dateString = "2024-06-01";
    String millisString = "1685577600000"; // corresponds to 2023-06-01T00:00:00Z approx

    // Act:
    Date parsedWithPattern = formatterWithPattern.parse(null, dateString);
    Date parsedWithoutPattern = formatterWithoutPattern.parse(null, millisString);

    // Assert:
    SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
    assertEquals(dateString, sdf.format(parsedWithPattern), "Date parsed with pattern should match expected date string");

    long expectedMillis = Long.parseLong(millisString);
    assertEquals(expectedMillis, parsedWithoutPattern.getTime(), "Date parsed without pattern should match expected milliseconds");
}
