@Test
public void testToString() {
    // Create a rectangle with known coordinates
    float top = 10f;
    float left = 20f;
    float width = 30f;
    float height = 40f;
    Rectangle rect = new Rectangle(top, left, width, height);

    // Get the string representation
    String str = rect.toString();

    // The string should start with the superclass toString() prefix (class@hashcode)
    // and end with ",bottom=,right=]"
    // We verify that the bottom and right values are correctly appended

    // Calculate expected bottom and right
    float expectedBottom = top + height;
    float expectedRight = left + width;

    // Check that the string contains the expected bottom and right values formatted as floats
    String expectedSuffix = String.format(",bottom=%f,right=%f]", expectedBottom, expectedRight);
    // The string should end with this suffix
    assertTrue("toString should end with bottom and right coordinates",
            str.endsWith(expectedSuffix));

    // Also check that the string starts with the superclass toString() part (class@hashcode)
    // which is the part before the last ']'
    int lastBracketIndex = str.lastIndexOf(']');
    assertTrue("toString should contain '[' before the suffix", lastBracketIndex > 0);
    String prefix = str.substring(0, lastBracketIndex);
    assertTrue("toString prefix should start with class name",
            prefix.startsWith(rect.getClass().getName() + "@"));
}
