@Test
public void testFindCells_basicGrid() {
    // Create horizontal rulings (y = 0, y = 10, y = 20)
    List<Ruling> horizontalRulings = new LinkedList<>();
    horizontalRulings.add(new Ruling(0, 0, 20, 0));   // top horizontal line
    horizontalRulings.add(new Ruling(0, 10, 20, 10)); // middle horizontal line
    horizontalRulings.add(new Ruling(0, 20, 20, 20)); // bottom horizontal line

    // Create vertical rulings (x = 0, x = 10, x = 20)
    List<Ruling> verticalRulings = new LinkedList<>();
    verticalRulings.add(new Ruling(0, 0, 0, 20));   // left vertical line
    verticalRulings.add(new Ruling(10, 0, 10, 20)); // middle vertical line
    verticalRulings.add(new Ruling(20, 0, 20, 20)); // right vertical line

    // Call the method under test
    List<Cell> cells = SpreadsheetExtractionAlgorithm.findCells(horizontalRulings, verticalRulings);

    // We expect 4 cells in a 2x2 grid
    // Cells are defined by top-left and bottom-right points:
    // (0,0)-(10,10), (10,0)-(20,10), (0,10)-(10,20), (10,10)-(20,20)
    assertEquals(4, cells.size());

    // Helper to check if a cell with given corners exists
    boolean containsCell = cells.stream().anyMatch(cell ->
        cell.getTopLeft().getX() == 0 && cell.getTopLeft().getY() == 0 &&
        cell.getBottomRight().getX() == 10 && cell.getBottomRight().getY() == 10);
    assertTrue(containsCell);

    containsCell = cells.stream().anyMatch(cell ->
        cell.getTopLeft().getX() == 10 && cell.getTopLeft().getY() == 0 &&
        cell.getBottomRight().getX() == 20 && cell.getBottomRight().getY() == 10);
    assertTrue(containsCell);

    containsCell = cells.stream().anyMatch(cell ->
        cell.getTopLeft().getX() == 0 && cell.getTopLeft().getY() == 10 &&
        cell.getBottomRight().getX() == 10 && cell.getBottomRight().getY() == 20);
    assertTrue(containsCell);

    containsCell = cells.stream().anyMatch(cell ->
        cell.getTopLeft().getX() == 10 && cell.getTopLeft().getY() == 10 &&
        cell.getBottomRight().getX() == 20 && cell.getBottomRight().getY() == 20);
    assertTrue(containsCell);
}
