    Table table = new Table(dummyAlgorithm);

    // Create a RectangularTextContainer chunk with some bounds and text
    Rectangle2D rect1 = new Rectangle2D.Double(0, 0, 10, 10);
    RectangularTextContainer chunk1 = new RectangularTextContainer(rect1);
    chunk1.setText("Cell 1");

    // Add chunk1 at (0,0)
    table.add(chunk1, 0, 0);

    // Verify rowCount and colCount updated
    assertEquals(1, table.getRowCount());
    assertEquals(1, table.getColCount());

    // Verify the cell at (0,0) is chunk1 (or equal)
    RectangularTextContainer cell00 = table.getCell(0, 0);
    assertNotNull(cell00);
