    // Add another chunk at (1,2)
    Rectangle2D rect2 = new Rectangle2D.Double(10, 10, 20, 20);
    RectangularTextContainer chunk2 = new RectangularTextContainer(rect2);
    chunk2.setText("Cell 2");
    table.add(chunk2, 1, 2);

    // Verify rowCount and colCount updated
    assertEquals(2, table.getRowCount());
    assertEquals(3, table.getColCount());

    // Verify the cell at (1,2) is chunk2
    RectangularTextContainer cell12 = table.getCell(1, 2);
